#Demande si combien des elements dans le tableau comporte de nombre
def nombreHandles(nH)

	number_count = 0
	for numero in nH do
	    if numero.match(/[0-9]/)
	      number_count +=1
	    end
	end
	puts "\n"
	puts "2- Combien d'handle contiennent un numéro ?"
	puts "\n"
	puts "=====> Il y en a #{number_count} qui contient de numero"
	puts "\n"
end
#nbr1 = liste
nombreHandles(nbr)

#Demande si combien des elements dans le tableau comporte de le mot aude

def cnombreAude(nAude)
  	count = 0
  	nAude.each do |n|
	    if n.match(/[a][u][d][e]/)
	      	count +=1
	    end
  	end
	puts "3- Combien d'handle contiennent les 4 lettres du prénom \"Aude\" à la suite (sans faire attention à la casse) ?"
	puts "\n"
	puts "=====> Il y en a #{count} handles qui contient aude"
	puts "\n"
end

cnombreAude(nbr)

#Demande si combien des elements dans le tableau commance par un majuscule

def nombreMaj(nM)
  	nbr = 0
  	for maj in nM
      	if maj.match(/@[A-Z]/)
      		nbr +=1
      	end
	end
	puts "4- Combien commencent par une majuscule ?"
	puts "\n"
	puts "=====> Il y a #{nbr} handles qui commance par majuscule!"
	puts "\n"
end

nombreMaj(nbr)

#Demande si combien des elements dans le tableau contient de majuscule

def nombreMajTab(nMT)
  	count = 0
