end

nombreMajTab(nbr)

#Nombre d'underscore

def nombreUnderscore(nU)
  	nbr = 0
  	for u in nU do
      	if u.match(/[_]/)
      		nbr +=1
      	end
  	end
  	puts "6- Combien d'underscore dans tous les pseudos confondus ?"
  	puts "\n"
  	puts "=====> Il y en a #{nbr} underscore"
  	puts "\n"
end

nombreUnderscore(nbr)

